  void access::object_traits_impl< ::MasterPoint, id_sqlite >::
  bind (sqlite::bind* b,
        const sqlite::bind* id,
        std::size_t id_size,
        image_type& i,
        sqlite::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace sqlite;

    std::size_t n (0);

    // id
    //
    if (sk == statement_insert)
    {
      if (id != 0)
        std::memcpy (&b[n], id, id_size * sizeof (id[0]));
      n += id_size;
    }

    // rw_flag
    //
    b[n].type = sqlite::bind::integer;
    b[n].buffer = &i.rw_flag_value;
    b[n].is_null = &i.rw_flag_null;
    n++;

    // return_flag
    //
    b[n].type = sqlite::bind::integer;
    b[n].buffer = &i.return_flag_value;
    b[n].is_null = &i.return_flag_null;
    n++;

    // id
    //
    if (sk == statement_update)
    {
      if (id != 0)
        std::memcpy (&b[n], id, id_size * sizeof (id[0]));
      n += id_size;
    }

    // TelemPoint base
    //
    if (sk == statement_select)
      base_traits::bind (b + n, *i.base, sk);
  }

