namespace odb
{
  static bool
  create_schema (database& db, unsigned short pass, bool drop)
  {
    ODB_POTENTIALLY_UNUSED (db);
    ODB_POTENTIALLY_UNUSED (pass);
    ODB_POTENTIALLY_UNUSED (drop);

    if (drop)
    {
      switch (pass)
      {
        case 1:
        {
          return true;
        }
        case 2:
        {
          db.execute ("DROP TABLE IF EXISTS \"SlavePoint\"");
          db.execute ("DROP TABLE IF EXISTS \"MasterPoint\"");
          db.execute ("DROP TABLE IF EXISTS \"TelemPoint\"");
          return false;
        }
      }
    }
    else
    {
      switch (pass)
      {
        case 1:
        {
          db.execute ("CREATE TABLE \"TelemPoint\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n"
                      "  \"typeid\" TEXT NOT NULL,\n"
                      "  \"name\" TEXT NOT NULL,\n"
                      "  \"register_address\" TEXT NOT NULL,\n"
                      "  \"data_type\" INT NOT NULL,\n"
                      "  \"value_type\" INT NOT NULL,\n"
                      "  \"bool_value\" INTEGER NOT NULL,\n"
                      "  \"int_value\" INTEGER NOT NULL,\n"
                      "  \"float_value\" REAL NOT NULL,\n"
                      "  \"timestamp\" INTEGER NOT NULL,\n"
                      "  \"unit\" TEXT NULL,\n"
                      "  \"request_flag\" INTEGER NOT NULL)");
          db.execute ("CREATE TABLE \"MasterPoint\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY,\n"
                      "  \"rw_flag\" INTEGER NOT NULL,\n"
                      "  \"return_flag\" INTEGER NOT NULL,\n"
                      "  CONSTRAINT \"id_fk\"\n"
                      "    FOREIGN KEY (\"id\")\n"
                      "    REFERENCES \"TelemPoint\" (\"id\")\n"
                      "    ON DELETE CASCADE)");
          db.execute ("CREATE TABLE \"SlavePoint\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n"
                      "  \"base_id\" INTEGER NOT NULL,\n"
                      "  CONSTRAINT \"base_id_fk\"\n"
                      "    FOREIGN KEY (\"base_id\")\n"
                      "    REFERENCES \"TelemPoint\" (\"id\")\n"
                      "    DEFERRABLE INITIALLY DEFERRED)");
          return false;
        }
      }
    }

    return false;
  }

  static const schema_catalog_create_entry
  create_schema_entry_ (
    id_sqlite,
    "",
    &create_schema);
}

